import React from 'react';
import "./Checkout.css";
import CheckoutProduct from './CheckoutProduct';
import { useStateValue } from './StateProvider';
import Subtotal from './Subtotal';
function Checkout() {
    const [{basket},dispatch]=useStateValue();
    return (
        <div className="checkout">
            <div className="checkout__left">
                <img className="checkout__ad" src="https://www.suricats-consulting.com/wp-content/uploads/2018/04/amazon-ad-platform-logo.png" alt="" />
                <h1 className="checkout__title">Your Shopping Basket</h1>
                {}
                <CheckoutProduct title="learnjs" image="https://interactive-examples.mdn.mozilla.net/media/cc0-images/grapefruit-slice-332-332.jpg" price={99.0} rating={5}  />
            </div>
            <div className="checkout__right">
                <Subtotal />
            </div>
        </div>
    )
}

export default Checkout
